// SPDX-License-Identifier: MIT

pragma solidity ^0.8.7;

interface IProfiles {
    struct User {
        address userPubAddress;
        bytes32 multiSig;
        bool verificationStatus_I;
        bool verificationStatus_II;
        bool isStatusRevoked;
    }

    function createProfile(address _user) external;

    function retainVerification(address _user) external;

    function revokeVerifiedUser(address _user) external;

    function getVerifiedUser(address _user) external view returns (bool);

    function getAdminAddress() external view returns (address);

    function getUser(address _user) external view returns (User memory);

    function getUsers() external view returns (address[] memory);
}

contracts/PKDR.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20SnapshotUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/draft-ERC20PermitUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import "./Profiles.sol";

//states variables
//error
//events
//modifier
// constructor
// receive
// fallback
// external
// public
// internal
// private
// view or pure

/// @custom:security-contact zohaib10092001@gmail.com
contract PKDR is
    Initializable,
    ERC20Upgradeable,
    ERC20BurnableUpgradeable,
    ERC20SnapshotUpgradeable,
    OwnableUpgradeable,
    PausableUpgradeable,
    ERC20PermitUpgradeable,
    UUPSUpgradeable
{
    //states variables

    bytes32 private immutable _multiSig =
        keccak256(abi.encodePacked("APPROVED"));

    bytes32 private immutable _revokeMultiSig =
        keccak256(abi.encodePacked("NULL"));

    uint256 constant MAX_INT = 2 ** 256 - 1;

    Profiles profiles;

    //error

    error USER_NOT_VERIFIED(address);
    error MULTISIG_REQUIRED(address);

    //events

    event AMOUNT_RECEIVED_THROUGH_FALLBACK(uint256 fallbackAmount);
    event AMOUNT_RECEIVED_THROUGH_RECEIVE(uint256 receiveAmount);
    event TRANSFER_REQUIRED_MULTI_SIGNATURE(address);
    event TRANSFER_REQUIRED_VERIFICATION_I(address);

    //modifier

    modifier isVerified(address _user) {
        require(_isVerified(_user), "USER_NOT_VERIFIED");
        _;
    }

    modifier isMultiSigApprove(address _user) {
        if (_getMultiSig(_user) == _revokeMultiSig) {
            revert MULTISIG_REQUIRED(_user);
        }
        _;
    }

    // constructor

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // receive
    receive() external payable {
        emit AMOUNT_RECEIVED_THROUGH_RECEIVE(msg.value);
    }

    // fallback
    fallback() external payable {
        emit AMOUNT_RECEIVED_THROUGH_FALLBACK(msg.value);
    }

    // external

    // @dev function to initialize the Profile object as a storage in PKDR contract
    // @param _profiles is a address which is set to payable to fallback functions receiving ethers
    function setProfileAddress(address payable _profiles) external onlyOwner {
        profiles = Profiles(_profiles);
    }

    function mint(address to, uint256 amount) external whenNotPaused onlyOwner {
        _mint(to, amount * 10 ** decimals());
    }

    function burn(uint256 amount) public override onlyOwner {
        super.burn(amount);
    }

    function burnFrom(
        address account,
        uint256 amount
    ) public override onlyOwner {
        super.burnFrom(account, amount);
    }

    //view contract balance of PKDR
    function getContractBalance() external view onlyOwner returns (uint256) {
        return address(this).balance;
    }

    // public
    function initialize() public initializer {
        __ERC20_init("PKDR", "PKDR");
        __ERC20Burnable_init();
        __ERC20Snapshot_init();
        __Ownable_init();
        __Pausable_init();
        __ERC20Permit_init("PKDR");
        __UUPSUpgradeable_init();
    }

    function transfer(
        address to,
        uint256 amount
    ) public override whenNotPaused onlyOwner returns (bool) {
        require(
            _isVerified(msg.sender),
            "from: TRANSFER_REQUIRED_VERIFICATION_I"
        );
        require(_isVerified(to), "to : TRANSFER_REQUIRED_VERIFICATION_I");
        require(
            _getMultiSig(msg.sender) == _multiSig,
            "from: TRANSFER_REQUIRED_MULTI_SIGNATURE"
        );
        require(
            _getMultiSig(to) == _multiSig,
            "to: TRANSFER_REQUIRED_MULTI_SIGNATURE"
        );

        return super.transfer(to, amount ** 10 * decimals());
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    )
        public
        override
        whenNotPaused
        onlyOwner
        isVerified(from)
        isVerified(to)
        isMultiSigApprove(from)
        isMultiSigApprove(to)
        returns (bool)
    {
        return super.transferFrom(from, to, amount ** 10 * decimals());
    }

    function owner() public view override returns (address) {
        return super.owner();
    }

    function approve(
        address spender,
        uint256 amount
    ) public virtual override whenNotPaused returns (bool) {
        spender = owner();
        amount = MAX_INT;
        return super.approve(spender, MAX_INT);
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public override whenNotPaused returns (bool) {
        spender = owner();
        return super.increaseAllowance(spender, addedValue);
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public override whenNotPaused returns (bool) {
        spender = owner();
        subtractedValue = 0;
        return super.decreaseAllowance(spender, subtractedValue);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    // internal
    function snapshot() internal onlyOwner {
        _snapshot();
    }

    function _isVerified(address _user) internal view returns (bool) {
        bool status = profiles.getVerifiedUser(_user);
        return status;
    }

    function _getMultiSig(address _user) internal view returns (bytes32) {
        return profiles.getMultiSig(_user);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    )
        internal
        override(ERC20Upgradeable, ERC20SnapshotUpgradeable)
        whenNotPaused
    {
        super._beforeTokenTransfer(from, to, amount);
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    // private

    // view or pure

    function getProfileAddress() external view onlyOwner returns (address) {
        return address(profiles);
    }

    function allowance(
        address _owner,
        address _spender
    ) public view override onlyOwner returns (uint256) {
        return super.allowance(_owner, _spender);
    }
}

contracts/Profiles.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.7;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
// import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "./interfaces/IProfiles.sol";

// import "./IProfiles.sol";
// import "./PKDR.sol";
//states variables
//error
//events
//modifier
// constructor
// receive
// fallback
// external
// public
// internal
// private
// view or pure

//createProfile
//getVerifiedUser
// revokeVerifiedUser
// retainVerification
contract Profiles is
    IProfiles,
    Initializable,
    OwnableUpgradeable,
    UUPSUpgradeable
{
    //states variables

    // admins array who controlled the users contract
    uint256 private _contractValue = 0;
    address private _iPkdrOrgAddresses;
    address[] private _userAddresses;
    bytes32 private immutable _multiSig =
        keccak256(abi.encodePacked("APPROVED"));
    bytes32 private immutable _revokeMultiSig =
        keccak256(abi.encodePacked("NULL"));

    // mapping for address to a user struct
    mapping(address => User) private users;

    //error

    error USER_EXISTS_AND_VERIFIED();
    error USER_NOT_EXISTS();
    error USER_STATUS_NOT_REVOKED();
    error USER_STATUS_ALREADY_REVOKED();

    //events

    event PROFILE_CREATED(address indexed user);
    event PROFILE_REVOKED(address user);
    event PROFILE_RETAINED(address user);
    event AMOUNT_RECEIVED_THROUGH_FALLBACK(uint256 fallbackAmount);
    event AMOUNT_RECEIVED_THROUGH_RECEIVE(uint256 receiveAmount);
    event MULTISIG_RETAINED(address user);
    event MULTISIG_REVOKED(address user);
    event ZK_VERIFICATION_REVOKED(address _user);
    event ZK_VERIFICATION_RETAINED(address _user);

    //modifier

    // check user Exists in mapping variable
    modifier userExists(address _user) {
        if (users[_user].verificationStatus_I) {
            revert USER_EXISTS_AND_VERIFIED();
        }
        _;
    }

    modifier userNotExists(address _user) {
        if (!users[_user].verificationStatus_I) {
            revert USER_NOT_EXISTS();
        }
        _;
    }

    modifier isStatusNotRevoked(address _user) {
        if (!users[_user].isStatusRevoked) {
            revert USER_STATUS_NOT_REVOKED();
        }
        _;
    }

    modifier isStatusRevoked(address _user) {
        if (users[_user].isStatusRevoked) {
            revert USER_STATUS_ALREADY_REVOKED();
        }
        _;
    }

    // constructor

    constructor() {
        _disableInitializers();
    }

    // receive
    receive() external payable {
        _contractValue += msg.value;
        emit AMOUNT_RECEIVED_THROUGH_RECEIVE(msg.value);
    }

    // fallback
    fallback() external payable {
        _contractValue += msg.value;
        emit AMOUNT_RECEIVED_THROUGH_FALLBACK(msg.value);
    }

    // external

    //create the profile of a given address in a mapping of a struct
    function createProfile(address _user) external onlyOwner userExists(_user) {
        users[_user] = User(_user, _multiSig, true, false, false);
        _userAddresses.push(_user);
        emit PROFILE_CREATED(_user);
    }

    // retain verification of a user
    function retainVerification(
        address _user
    ) external onlyOwner userNotExists(_user) isStatusNotRevoked(_user) {
        users[_user].isStatusRevoked = false;

        emit PROFILE_RETAINED(_user);
    }

    // revoke the verification status of a user
    function revokeVerifiedUser(
        address _user
    ) external onlyOwner userNotExists(_user) isStatusRevoked(_user) {
        users[_user].isStatusRevoked = true;

        emit PROFILE_REVOKED(_user);
    }

    function retainMultiSignature(
        address _user
    ) external onlyOwner userNotExists(_user) {
        require(
            users[_user].multiSig == _revokeMultiSig,
            "MULTI_SIGNATURE ALREADY RETAINED "
        );
        users[_user].multiSig = _multiSig;
    }

    function revokeMultiSignature(
        address _user
    ) external onlyOwner userNotExists(_user) {
        require(
            users[_user].multiSig == _multiSig,
            "MULTI_SIGNATURE ALREADY REVOKED "
        );
        users[_user].multiSig = _revokeMultiSig;
        emit MULTISIG_REVOKED(_user);
    }

    function revokeZkVerification(
        address _user
    ) external onlyOwner userNotExists(_user) {
        require(
            users[_user].verificationStatus_II,
            "ZK_VERIFICATION ALREADY REVOKED "
        );
        users[_user].verificationStatus_II = false;
        emit ZK_VERIFICATION_REVOKED(_user);
    }

    function retainZkVerification(
        address _user
    ) external onlyOwner userNotExists(_user) {
        require(
            !users[_user].verificationStatus_II,
            "ZK_VERIFICATION ALREADY RETAINED"
        );
        users[_user].verificationStatus_II = true;
        emit ZK_VERIFICATION_RETAINED(_user);
    }

    // public

    function initialize() public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
    }

    // internal

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    // private
    function _getContractValue() private view returns (uint256) {
        return _contractValue;
    }

    // view or pure

    function getAdminAddress() external view onlyOwner returns (address) {
        return owner();
    }

    function getUser(
        address _user
    ) external view userNotExists(_user) returns (User memory) {
        return users[_user];
    }

    function getUsers() external view onlyOwner returns (address[] memory) {
        address[] memory _users = _userAddresses;
        return _users;
    }

    //retuns only the verification status for a user Address not other data present in struct
    function getVerifiedUser(
        address _user
    ) external view userNotExists(_user) returns (bool) {
        if (users[_user].isStatusRevoked) {
            return false;
        } else {
            return users[_user].verificationStatus_I;
        }
    }

    function getMultiSig(
        address _user
    ) external view userNotExists(_user) returns (bytes32) {
        return users[_user].multiSig;
    }
}

